CREATE FUNCTION createcomponent(vcomponentparentuuid uuid, vcomponentname character varying, vcomponentdescription character varying, vcreatedby uuid, vroles text[]) RETURNS TABLE(componentuuid uuid, componentname character varying, componentparentname character varying, componentparentuuid uuid, componentdecription character varying, createdat timestamp with time zone, createdby uuid) LANGUAGE plpgsql AS $$ #variable_conflict use_column DECLARE vComponentID integer := (select nextval('ComponentID')); vComponentUUID uuid := (select uuid_generate_v4()); vComponentParentID integer := (select componentid from components where componentuuid = vComponentParentUUID); vFunctionName varchar := 'CreateComponent'; vIsAllowed boolean := (select public.checkPermissions(vRoles, vFunctionName)); BEGIN
IF(vIsAllowed) THEN
INSERT INTO components(ComponentID, ComponentUUID, ComponentParentID, ComponentName, ComponentDescription, CreatedBy, CreatedAt) VALUES(vComponentID, vComponentUUID, vComponentParentID, vComponentName, vComponentDescription, vCreatedBy, now());
ELSE RAISE EXCEPTION '%', 'Insufficiency rigths'; RETURN; END IF; -- Begin Log if success perform public.createlog(0,'Created Component sucessfully', 'CreateComponent', 'ComponentID: ' || cast(vComponentID as varchar) || ', ' || 'ComponentParentID: ' || cast(vComponentParentID as varchar) || ', ComponentName: ' || vComponentName || ', ComponentDescription: ' || vComponentDescription || ', CreatedBy: ' || cast(vCreatedBy as varchar));
-- End Log if success -- Return RETURN QUERY ( select co.ComponentUUID, co.ComponentName, cs.ComponentName as componentParentName, cs.ComponentUUID as componentParentUUID, co.ComponentDescription, co.CreatedAt at time zone 'utc', vCreatedBy from components co left outer join components cs on co.componentparentid = cs.componentid where co.componentuuid = vComponentUUID );
exception when others then -- Begin Log if error perform public.createlog(1,'ERROR: ' || SQLERRM || ' ' || SQLSTATE, 'CreateComponent', 'ComponentID: ' || cast(vComponentID as varchar) || ', ' || 'ComponentParentID: ' || cast(vComponentParentID as varchar) || ', ComponentName: ' || vComponentName || ', ComponentDescription: ' || vComponentDescription || ', CreatedBy: ' || cast(vCreatedBy as varchar)); -- End Log if error RAISE EXCEPTION '%', 'ERROR: ' || SQLERRM || ' ' || SQLSTATE || ' at CreateComponent'; RETURN; END; $$;

