CREATE FUNCTION getalloffers(vuseruuid character varying, vroles text[]) RETURNS TABLE(offeruuid uuid, paymentinvoiceuuid uuid, createdat timestamp with time zone, createdby uuid)
    LANGUAGE plpgsql
    AS $$

	DECLARE
		vFunctionName varchar := 'GetAllOffers';
		vIsAllowed boolean := (select public.checkPermissions(vRoles, vFunctionName));

	BEGIN

	IF(vIsAllowed) THEN

	RETURN QUERY (SELECT  offeruuid,
				paymentinvoiceuuid,
				offr.createdat at time zone 'utc',
				offr.createdby
			FROM offer offr JOIN
			paymentinvoice pi ON offr.paymentinvoiceid = pi.paymentinvoiceid
		);

	ELSE
		 RAISE EXCEPTION '%', 'Insufficiency rigths';
		 RETURN;
	END IF;

	END;
    $$;

