CREATE FUNCTION getalltechnologydata(vuseruuid uuid, vroles text[]) RETURNS TABLE(technologydatauuid uuid, technologyuuid uuid, technologydataname character varying, technologydata character varying, technologydatadescription character varying, productcode integer, licensefee bigint, technologydatathumbnail bytea, technologydataimgref character varying, createdat timestamp with time zone, createdby uuid, updatedat timestamp with time zone, useruuid uuid)
    LANGUAGE plpgsql
    AS $$

	DECLARE vFunctionName varchar := 'GetAllTechnologyData';

		vIsAllowed boolean := (select public.checkPermissions(vRoles, vFunctionName));



	BEGIN



	IF(vIsAllowed) THEN



		RETURN QUERY (SELECT 	technologydatauuid,

					tc.technologyuuid,

					td.technologydataname,

					technologydata,

					technologydatadescription,

					licensefee,

					td.productcode,

					technologydatathumbnail,

					technologydataimgref,

					td.createdat  at time zone 'utc',

					td.createdBy,

					td.updatedat  at time zone 'utc',

					td.UpdatedBy

			FROM TechnologyData td

			join technologies tc

			on td.technologyid = tc.technologyid

			);



	ELSE

		 RAISE EXCEPTION '%', 'Insufficiency rigths';

		 RETURN;

	END IF;



	END;

	$$;

