CREATE FUNCTION getcomponentsbytechnology(vtechnologyuuid uuid, vuseruuid uuid, vroles text[]) RETURNS TABLE(componentuuid uuid, componentname character varying, componentparentuuid uuid, componentparentname character varying, componentdescription character varying, createdat timestamp with time zone, createdby uuid, updatedat timestamp with time zone, useruuid uuid) LANGUAGE plpgsql AS $$ DECLARE vFunctionName varchar := 'GetComponentsByTechnology'; vIsAllowed boolean := (select public.checkPermissions(vRoles, vFunctionName));
BEGIN
IF(vIsAllowed) THEN
RETURN QUERY (select co.componentUUID, co.componentName, cs.componentUUID as componentParentUUID, cs.componentName as componentParentName, co.componentDescription, co.createdat at time zone 'utc', co.createdby, co.updatedat at time zone 'utc', co.updatedby from components co join componentstechnologies ct on co.componentid = ct.componentid join technologies tc on tc.technologyid = ct.technologyid left outer join components cs on co.componentparentid = cs.componentid where tc.technologyuuid = vTechnologyUUID );
ELSE RAISE EXCEPTION '%', 'Insufficiency rigths'; RETURN; END IF;
END; $$;

