CREATE FUNCTION getcomponentsfortechnologydataid(vtechnologydatauuid uuid, vuseruuid uuid, vroles text[]) RETURNS TABLE(componentuuid uuid, componentname character varying, componentparentuuid uuid, componentparentname character varying, componentdescription character varying, createdat timestamp with time zone, createdby uuid, updatedat timestamp with time zone, useruuid uuid) LANGUAGE plpgsql AS $$ DECLARE vFunctionName varchar := 'GetComponentsForTechnologyDataId'; vIsAllowed boolean := (select public.checkPermissions(vRoles, vFunctionName));
BEGIN
