RETURN QUERY (select co.componentuuid, co.componentname, cp.componentuuid as ComponentParentUUID, cp.componentname as ComponentParentName, co.componentDescription, co.createdat at time zone 'utc', td.CreatedBy, co.updatedat at time zone 'utc', td.UpdatedBy from technologydata td join technologydatacomponents tc on td.technologydataid = tc.technologydataid join components co on co.componentid = tc.componentid join components cp on co.componentparentid = cp.componentid where td.technologydatauuid = vTechnologyDataUUID ); ELSE RAISE EXCEPTION '%', 'Insufficiency rigths'; RETURN; END IF;
END; $$;

