CREATE FUNCTION getlicensefeebytechnologydata(vtechnologydatauuid uuid, vuseruuid uuid, vroles text[]) RETURNS TABLE(licensefee bigint)
    LANGUAGE plpgsql
    AS $$

	DECLARE

		vFunctionName varchar := 'GetLicenseFeeByTechnologyData';

		vIsAllowed boolean := (select public.checkPermissions(vRoles, vFunctionName));



	BEGIN



	IF(vIsAllowed) THEN



	RETURN QUERY (select	td.licenseFee

			from technologydata td

			where td.technologydatauuid = vTechnologyDataUUID

		);

	ELSE

		 RAISE EXCEPTION '%', 'Insufficiency rigths';

		 RETURN;

	END IF;



	END;

$$;

