div.pre:
CREATE FUNCTION getofferbyid(vofferid uuid, vuseruuid uuid, vroles text[]) RETURNS TABLE(offeruuid uuid, paymentinvoiceuuid uuid, createdat timestamp with time zone, createdby uuid)
    LANGUAGE plpgsql
    AS $$

	DECLARE
		vFunctionName varchar := 'GetOfferById';
		vIsAllowed boolean := (select public.checkPermissions(vRoles, vFunctionName));

	BEGIN

	IF(vIsAllowed) THEN

	RETURN QUERY (SELECT  ofr.offeruuid,
				pm.paymentinvoiceuuid,
				ofr.createdat at time zone 'utc',
				ofr.createdby
			FROM offer ofr
			JOIN paymentinvoice pm
			ON ofr.paymentinvoiceid = pm.paymentinvoiceid
			WHERE ofr.offeruuid = vOfferID
		);
	ELSE
		 RAISE EXCEPTION '%', 'Insufficiency rigths';
		 RETURN;
	END IF;

	END;
    $$;

