CREATE FUNCTION getofferforpaymentinvoice(vpaymentinvoiceuuid uuid, vuseruuid uuid, vroles text[]) RETURNS TABLE(offeruuid uuid, paymentinvoiceuuid uuid, createdat timestamp with time zone, createdby uuid)
    LANGUAGE plpgsql
    AS $$
	DECLARE
		vFunctionName varchar := 'GetOfferForPaymentInvoice';
		vIsAllowed boolean := (select public.checkPermissions(vRoles, vFunctionName));

	BEGIN

	IF(vIsAllowed) THEN

	RETURN QUERY (select 	ofr.offerUUID,
				pi.PaymentInvoiceUUID,
				pi.createdat at time zone 'utc',
				ofr.createdby
			from Offer ofr
			join paymentinvoice pi
			on ofr.paymentinvoiceid = pi.paymentinvoiceid
		);

	ELSE
		 RAISE EXCEPTION '%', 'Insufficiency rigths';
		 RETURN;
	END IF;

	END;
$$;

