div.pre:
CREATE FUNCTION getpaymentinvoiceforofferrequest(vofferrequestuuid uuid, vuseruuid uuid, vroles text[]) RETURNS TABLE(paymentinvoiceuuid uuid, offerrequestuuid uuid, invoice character varying, createdat timestamp with time zone, createdby uuid)
    LANGUAGE plpgsql
    AS $$

	DECLARE
		vFunctionName varchar := 'GetPaymentInvoiceForOfferRequest';
		vIsAllowed boolean := (select public.checkPermissions(vRoles, vFunctionName));

	BEGIN

	IF(vIsAllowed) THEN

	RETURN QUERY (select 	pi.paymentinvoiceuuid,
							oq.offerrequestuuid,
							pi.invoice,
							pi.createdat at time zone 'utc',
							pi.createdby
				from PaymentInvoice pi
				join offerrequest oq
				on pi.offerrequestid = oq.offerrequestid
				where oq.offerRequestUUID = vOfferRequestUUID
		);
	ELSE
		 RAISE EXCEPTION '%', 'Insufficiency rigths';
		 RETURN;
	END IF;

	END;
$$;

