CREATE FUNCTION gettagbyid(vtagid uuid, vuseruuid uuid, vroles text[]) RETURNS TABLE(taguuid uuid, tagname character varying, createdat timestamp with time zone, createdby uuid, updatedat timestamp with time zone, updatedby uuid)
    LANGUAGE plpgsql
    AS $$

	DECLARE
		vFunctionName varchar := 'GetTagById';
		vIsAllowed boolean := (select public.checkPermissions(vRoles, vFunctionName));

	BEGIN

	IF(vIsAllowed) THEN

	RETURN QUERY (SELECT  taguuid,
				tagname,
				tg.createdat  at time zone 'utc',
				tg.createdby,
				tg.updatedat  at time zone 'utc',
				tg.updatedby
		    FROM tags tg
		    WHERE tagUUID = vTagID
		  );

	ELSE
		 RAISE EXCEPTION '%', 'Insufficiency rigths';
		 RETURN;
	END IF;

	END;
    $$;

