CREATE FUNCTION gettechnologybyname(vtechname character varying, vuseruuid uuid, vroles text[]) RETURNS TABLE(technologyuuid uuid, technologyname character varying, technologydescription character varying, createdat timestamp with time zone, createdby uuid, updatedat timestamp with time zone, updatedby uuid)
    LANGUAGE plpgsql
    AS $$

			DECLARE
				vFunctionName varchar := 'GetTechnologyByName';
				vIsAllowed boolean := (select public.checkPermissions(vRoles, vFunctionName));

			BEGIN

			IF(vIsAllowed) THEN

			RETURN QUERY (SELECT  	tg.technologyuuid,
						tg.technologyname,
						tg.technologydescription,
						tg.createdat at time zone 'utc',
						tg.createdby,
						tg.updatedat at time zone 'utc',
						tg.updatedby
				    FROM Technologies tg
				    WHERE lower(tg.technologyname) = lower(vtechName)
				 );

			ELSE
				 RAISE EXCEPTION '%', 'Insufficiency rigths';
				 RETURN;
			END IF;

			END;
		    $$;

