CREATE FUNCTION gettechnologydataownerbyid(vtechnologydatauuid uuid, vuseruuid uuid, vroles text[]) RETURNS TABLE(createdby character varying)
    LANGUAGE plpgsql
    AS $$
	DECLARE
		vFunctionName varchar := 'GetTechnologyDataOwnerById';
		vIsAllowed boolean := (select public.checkPermissions(vRoles, vFunctionName));

	BEGIN

	IF(vIsAllowed) THEN

	RETURN QUERY (	SELECT 	td.createdby
				FROM TechnologyData td
				where technologydatauuid = vtechnologydatauuid
				and td.deleted is null
		);

	ELSE
		 RAISE EXCEPTION '%', 'Insufficiency rigths';
		 RETURN;
	END IF;

	END;
$$;

