CREATE FUNCTION gettopcomponents(vfrom timestamp with time zone, vto timestamp with time zone, vlimit integer, vuseruuid uuid, vroles text[]) RETURNS TABLE(componentname character varying, amount integer) LANGUAGE plpgsql AS $$
DECLARE vFunctionName varchar := 'GetTopComponents'; vIsAllowed boolean := (select public.checkPermissions(vRoles, vFunctionName));
BEGIN
IF(vIsAllowed) THEN RETURN QUERY ( with activatedLincenses as( select co.componentname, activatedat from licenseorder lo join offer of on lo.offerid = of.offerid join paymentinvoice pi on of.paymentinvoiceid = pi.paymentinvoiceid join offerrequest oq on pi.offerrequestid = oq.offerrequestid join offerrequestitems ri on oq.offerrequestid = ri.offerrequestid join technologydata td on ri.technologydataid = td.technologydataid join technologydatacomponents tc on tc.technologydataid = td.technologydataid join components co on co.componentid = tc.componentid ), rankTable as ( select a.componentname, count(a.componentname) as rank from activatedLincenses a where activatedat between vFrom and vTo group by a.componentname) select r.componentname::varchar(250), rank::integer from rankTable r order by rank desc limit vLimit);
ELSE RAISE EXCEPTION '%', 'Insufficiency rigths'; RETURN; END IF;
END; $$;

head.script:
