CREATE FUNCTION gettoptechnologydata(vfrom timestamp with time zone, vto timestamp with time zone, vlimit integer, vcreatedby uuid, vroles text[]) RETURNS TABLE(year text, month text, day text, hour text, technologydataname text, amount integer, revenue bigint) LANGUAGE plpgsql AS $$
DECLARE
vFunctionName varchar := 'GetTopTechnologyData';
vIsAllowed boolean := (select public.checkPermissions(vRoles, vFunctionName));
BEGIN
IF(vIsAllowed) THEN
RETURN QUERY (select r.year, r.month, r.day, r.hour, coalesce(td.technologydataname,'Total')::text as technologydataname, r.amount, r.revenue from
getrevenue(vFrom,
vTo,
null,
vCreatedBy,
vRoles) r
join technologydata td
on td.technologydatauuid = r.technologydatauuid
where r.year = '0'
and r.month = '0'
and r.day = '0'
and r.hour = '0'
and td.technologydataname <> 'Total'
and td.deleted is null
order by r.amount desc, td.technologydataname
limit vLimit
);
ELSE
RAISE EXCEPTION '%', 'Insufficiency rigths';
RETURN;
END IF;
END;
$$;

