CREATE FUNCTION gettotaluserrevenue(vfrom timestamp with time zone, vto timestamp with time zone, vcreatedby uuid, vroles text[]) RETURNS TABLE(date date, hour text, technologydataname text, amount integer, revenue bigint)
    LANGUAGE plpgsql
    AS $$



			DECLARE

				vFunctionName varchar := 'GetTotalUserRevenue'; 

				vIsAllowed boolean := (select public.checkPermissions(vRoles, vFunctionName));

				vTechnologyDataUUID uuid := (select uuid_nil());

			BEGIN 



			IF(vIsAllowed) THEN



			RETURN QUERY (select  to_date(r.year || '-' || r.month || '-' || r.day, 'YYYY-MM-DD') as date, r.hour::text, coalesce(td.technologydataname,'Total')::text as technologydataname, r.amount, r.revenue from public.getrevenue(

					    vFrom,

					    vTo,

					    vTechnologyDataUUID,

