					    left outer join technologydata td

					    on td.technologydatauuid = r.technologydatauuid

					    where r.year = '0'

					    and r.month = '0'

					    and r.day = '0'

					    and r.hour = '0'

					    and r.technologydatauuid = vTechnologyDataUUID			    

					    order by r.year asc, r.month asc, r.day asc

				);

			ELSE

				 RAISE EXCEPTION '%', 'Insufficiency rigths';

				 RETURN;

			END IF;



			END;

		$$;

