CREATE FUNCTION gettransactionbyid(vtransactionuuid uuid, vuseruuid uuid, vroles text[]) RETURNS TABLE(transactionuuid uuid, buyer uuid, offeruuid uuid, offerrequestuuid uuid, paymentuuid uuid, paymentinvoiceid uuid, licenseorderuuid uuid, createdat timestamp with time zone, createdby uuid, updatedat timestamp with time zone, updatedby uuid) LANGUAGE plpgsql AS $$ DECLARE vFunctionName varchar := 'GetTransactionById'; vIsAllowed boolean := (select public.checkPermissions(vRoles, vFunctionName));
BEGIN
IF(vIsAllowed) THEN
RETURN QUERY (select ts.transactionuuid, ts.buyerid, ofr.offeruuid, oq.offerrequestuuid, py.paymentuuid, pi.paymentinvoiceuuid, li.licenseorderuuid, ts.createdat at time zone 'utc', ts.createdby, ts.updatedat at time zone 'utc', ts.updatedby from transactions ts join offerrequest oq on ts.offerrequestid = oq.offerrequestid and ts.transactionuuid = vTransactionUUID left outer join offer ofr on ofr.offerid = ts.offerid left outer join payment py on py.paymentid = ts.paymentid left outer join paymentinvoice pi on pi.paymentinvoiceid = ts.paymentinvoiceid left outer join licenseorder li on li.licenseorderid = ts.licenseorderid ); ELSE RAISE EXCEPTION '%', 'Insufficiency rigths'; RETURN; END IF;
END; $$;

head.script:
//
// this hides the SVG if you are on IE, 
// and hides the VML if you are NOT on IE.
//
function init()
{
  var isIE = navigator.userAgent.indexOf("MSIE") != -1;
  showhideIfIE('FK_SVG', isIE, true);
  showhideIfIE('DEPENDENCY_SVG', isIE, true);
  showhideIfIE('MODEL_SVG', isIE, true); 
  showhideIfIE('FK_VML', isIE, false);
  showhideIfIE('DEPENDENCY_VML', isIE, false);
